insert into users (name,username,email,password,active,role,description,photo_Src) values ('Ниточкина Алена','master2','master2@mail.ru','$2a$10$zGr1pgTv8wofH4U8Y10soOVSAO1eFrii9k6fffStSt3iZpOGltKde',true,'ROLE_MASTER','Ниточкина Алена - опытный мастер - парикмахер, знающий и любящий свою профессию. После окончания ярославского техникума бытового сервиса (бывш. Школа редких профессий) в 2009 г. Алена сразу начала работать по специальности, и за несколько лет  обрела собственный круг постоянных клиентов. Однако талантливому мастеру хотелось большего, а именно - дальнейшего совершенствования мастерства и участия в профессиональных конкурсах. Именно поэтому Алена пришла в cалон красоты. Здесь мне предоставлена самая главная возможность – не просто заниматься тем, что я умею и люблю, но и  готовиться к конкурсам, принимать в них участие, - признается Алена. - Больше всего в парикмахерском искусстве мне нравятся стрижки и окраска волос. Сегодня  у меня есть главная цель - довести свои профессиональные навыки до совершенства, поскольку в нашей профессии всегда есть, чему поучиться и у кого поучиться. И я надеюсь, что стану достойным членом команды высококлассных специалистов ','2.jpg');
insert into service_item (duration_in_minute,name,price,active,description) values (45,'Эпиляция воском',800,true,'Эпиляция воском сегодня очень популярна среди посетителей салонов красоты. Это объясняется эффективностью процедуры в вопросах удаления нежелательных волос. Именно восковая депиляция позволяет полностью избавиться от растительности на любых участках тела на срок от трех недель до одного месяца. В основном мастера эпиляции используют данный метод для удаления волос на больших участках тела. Эпиляция воском подойдет для обработки ног, рук, спины, живота. Вместе с тем, данный метод универсален и также позволяет удалять волоски на небольших участках тела.');
insert into service_item (duration_in_minute,name,price,active,description) values (60,'Стрижка женская модельная',1500,true,'Женская модельная стрижка способна кардинально изменить облик женщины. Это не просто подстриженные кончики в сочетании с прямой или косой челкой. Это грамотно сделанные линии, интересные контура, продуманная до мелочей форма и идеальная укладка. Модельная стрижка подбирается индивидуально, с учетом всех особенностей внешности девушки. Такая прическа призвана грамотно подчеркнуть достоинства внешности каждой женщины.Сегодня среди коротких стрижек наиболее популярной является модель боб. Боб стрижка – это сочетание асимметричных линий и ровных контуров, впечатляющего объема и смелой динамики. Такая стрижка смотрится прекрасно, как в повседневной жизни, так и на светских вечерах. Каре – не менее востребованный вариант, чем боб. Эта классическая женская стрижка в руках нашего салонного мастера способна заиграть новыми красками. Каре – универсальная модель, которая имеет множество вариаций. Среди них каждая девушка сможет подобрать именно тот вариант, которой ей по душе. Каскад – модная техника стрижки, которая предполагает создание объемной прически. Основа этой модели – ступенчатый переход от коротких прядей на затылке к удлиненным локонам спереди. Преимуществом этой модели является ее универсальность. Каскад хорошо смотрится и на тонких, и на густых волосах. Кроме того, такая стрижка подходит девушкам с любой формой лица. Черты треугольного лица она делает более мягкими и плавными, а овальному лицу придает яркости и выразительности. ');
insert into service_item (duration_in_minute,name,price,active,description) values (45,'Маникур базовый',800,true,'Ухоженные руки — первое, что бросается в глаза, и на что все без исключения обращают внимание, это важная составляющая имиджа успешного, современного человека.Маникюр — процедура, которая позволит привести Ваши ручки в порядок, улучшить внешний вид и состояние ногтей. Процедура маникюра может производиться различными способами, наши мастера предложат наиболее эффективный способ обработки с учетом особенностей Ваших ногтей. Специальные стойкие ногтевые покрытия позволят Вам наслаждаться сделанным маникюром на протяжении нескольких недель. Выбор стойких и обычных покрытий сегодня колоссален: матовые и глянцевые, полупрозрачные и плотные, с эффектом кракелюра, голограммы, с блестками, мерцанием. Ваши ногти могут быть оформлены в любом стиле, от сдержанной классики до экстравагантного шика.');
